mapper.select:
SELECT
    info_id, user_id, name, school_class, sex, grade, specialty, college, specialty_direction, user_name
FROM
    `student_info`
LEFT JOIN
    sys_user
ON
    sys_user.id = student_info.user_id
where
    sys_user.status = 0

    and user_name LIKE #{condition.userName}


    and name LIKE #{condition.name}


    and school_class LIKE #{condition.schoolClass}


    and sex LIKE #{condition.sex}


    and grade LIKE #{condition.grade}


    and specialty LIKE #{condition.specialty}


    and college LIKE #{condition.college}


    and specialty_direction LIKE #{condition.specialtyDirection}

SELECT
student_test.id, info_id, student_info.user_id, name, school_class, sex, grade, specialty, college, specialty_direction, user_name
FROM
    `student_test`
LEFT JOIN
    student_info
ON
    student_test.user_id = student_info.user_id
LEFT JOIN
    sys_user
ON
    sys_user.id = student_info.user_id
WHERE
    student_test.test_id = #{id} and sys_user.status = 0

    and user_name LIKE #{condition.userName}


    and name LIKE #{condition.name}


    and school_class LIKE #{condition.schoolClass}


    and sex LIKE #{condition.sex}


    and grade LIKE #{condition.grade}


    and specialty LIKE #{condition.specialty}


    and college LIKE #{condition.college}


    and specialty_direction LIKE #{condition.specialtyDirection}
